INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-01', 'AI','https://techcrunch.com/category/artificial-intelligence/','News coverage on artificial intelligence and machine learning tech, the companies building them, and the ethical issues AI raises today. This encompasses generative AI, including large language models, text-to-image and text-to-video models; speech recognition and generation; and predictive analytics.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-02', 'Apps','https://techcrunch.com/category/apps/','The app economy continues to grow, having produced a record number of downloads and consumer spending across both the iOS and Google Play stores. Keep up with this fast-moving industry in one place, with the latest from the world of apps, including news, updates, startup fundings, mergers and acquisitions, and much more.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-03', 'Biotech and Health','https://techcrunch.com/category/biotech-health/','<span data-sheets-userformat="{" data-sheets-value="{">Read the latest news about biotech and health, from the latest health tech and medical devices to research in synthetic biology and genetics. Our biotech coverage has startups and research from around the world in context with industry and investment trends.</span>');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-04', 'Climate','https://techcrunch.com/category/climate/','Our climate news coverage consists of the latest startups and investments in solar energy, greentech, renewables, food tech, alternative proteins, as well as circular economy and carbon capture efforts. We also cover research into climate change and how technology can be used to track and mitigate severe weather and climate-related disasters.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-05', 'Commerce','https://techcrunch.com/category/commerce/','Retail, online shopping, e-commerce, and social commerce are a huge and ever-changing industry. Our commerce news covers everyone from Amazon, Shopify and Walmart to all the newest and hottest DTC brands, as well as influencers making the shift into selling products online.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-06', 'Crypto','https://techcrunch.com/category/cryptocurrency/','Cryptocurrency coverage and news on Bitcoin, Ethereum and the blockchain startups building the future of crypto, web3 using tokens and NFTs.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-07', 'Enterprise','https://techcrunch.com/category/enterprise/','Read the latest news on enterprise, from new products to large SaaS providers like Salesforce, Adobe, ServiceNow and Atlassian to funding for small SaaS companies and new products that help startups build their own SaaS.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-08', 'Fintech','https://techcrunch.com/category/fintech/','Read the latest news and coverage of fintech, which stands for "financial technology," focusing on everything from disruptor banks and innovations at established financial institutions to expense management startups, P2P payment services, checkout tools and the technologies that enable them.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-09', 'Gadgets','https://techcrunch.com/category/gadgets/','News, updates and reviews on the latest gadgets in tech. Coverage includes smartphones, wearables, laptops, drones and all of your consumer electronics needs.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-10', 'Gaming','https://techcrunch.com/category/gaming/','Mobile gaming is a behemoth in terms of annual revenue, and our gaming news coverage includes iOS, Android, mobile web and more, including companies like Netflix trying to get a foot in the door. We also report on the AAA gaming studios, which are increasingly a target for acquisitive giants like Microsoft and Sony.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-11', 'Government and Policy','https://techcrunch.com/category/government-policy/','Technology is deeply intertwined with government and policy, both being regulated by them and augmenting civic infrastructure like voting, social services, the justice system, and state-funded research, as well as exploring the frontiers of civil rights.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-12', 'Hardware','https://techcrunch.com/category/hardware/','Product news and hardware reviews, focusing on the latest hardware innovations from the latest startups to the biggest players like Apple, Samsung, Amazon Google, Microsoft and DJI, from smartphones, smartwatches and smart homes to drones, connected fitness, laptops, wearables and AR/VR.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-13', 'Media and Entertainment','https://techcrunch.com/category/media-entertainment/','Media and Entertainment tech news covers everything from Big Tech to streaming services to mobile apps. We dissect startups entering App Stores, social media, the creator economy and more.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-14', 'Privacy','https://techcrunch.com/category/privacy/','Stay up to date on privacy news coverage, from data protection, policy and emerging tech, and how startups are trying to solve some of the biggest data privacy problems for the future.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-15', 'Robotics','https://techcrunch.com/category/robotics/','Bleeding edge robotics companies focused on a wide range of automation categories, including logotics, agtech, delivery, healthcare, home robotics and food service, along with the latest university and laboratory research into soft robotics, nano robotics and more.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-16', 'Security','https://techcrunch.com/category/security/','Security news coverage encompasses investigative cybersecurity reporting and analysis on the latest security breaches, hacks and cyberattacks around the globe.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-17', 'Social','https://techcrunch.com/category/social/','Social networking shapes society and our news coverage reflects and directs that. We keep tabs on all the biggest social companies, including TikTok, Facebook, Instagram, Twitter, Snapchat and Reddit, as well as funding and acquisitions of new social startups.');
INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-18', 'Space','https://techcrunch.com/category/space/','The latest news and updates on space, including launches and funding rounds about satellite communications systems like Starlink and Kuiper, and of course the latest research taking place on the ISS, the Moon, Mars and beyond.');
