INSERT INTO Article_Category (ID_Category,Name_Category,URL_Category,Description_) VALUES ('Cat-21', 'Transportation','https://techcrunch.com/category/transportation/','Transportation news includes all the present and future ways people and packages get from Point A to Point B. Coverage includes scooters and e-bikes to autonomous vehicles, EVs, transit, and evTOLs. We cover auto tech players big and small, from Tesla, GM, Uber, and Lyft, to small startups entering the automotive tech space.');
